var scrollHeight=$("body").height();function showBackToTop(){var a=document.documentElement.scrollTop||window.pageYOffset||document.body.scrollTop;if(a>=scrollHeight){$("#backTop").show()}else{$("#backTop").hide()}}showBackToTop();$(window).scroll(function(){showBackToTop()});function backToTop(){$("#backTop").click(function(){$("html, body").animate({scrollTop:0})})}backToTop();